package iftest;

public class SwitchTest03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	String position = "과장";
		
		switch(position) {
		case "사장":
			System.out.println("천만원");
			break;
		case "부장":
			System.out.println("칠백만원");
			break;
		case "과장":
			System.out.println("오백만원");
			break;
		case "차장":
			System.out.println("사백만원");
			break;
		}

	}

}
